import React, { Component } from 'react'
import { Row, Col, Input, Button, Icon } from 'antd';
import { Card, Menu, Breadcrumb, Typography } from 'antd';
import List from "../components/Usefull/List"
import BackButton from "../components/Usefull/BackButton"
import MenuLevel from '../components/Usefull/MenuLevel'
import Axios from "../config/axios.setup"

export class SelectionQuestion extends Component {

  state ={
    questionList:[]
  }
  componentDidMount() {
    Axios.get("/showquestion")
.then(result =>{
  const questionList = result.data;
  this.setState({ questionList });
})
.then(console.log(this.state,"state is"))
  }

  filterProducts() {
    const id = this.props.Google.selectQuestionLevel;
    console.log("this.props.questionList is", this.props.Google.questionList)
    console.log(id, "id is")
    if (id == null) {
      return []
    } else {
      return this.props.Google.questionList.filter(product => product.Difficulty == id)
    }
  }

  renderListQuestion = () => {
    let checklv = this.filterProducts()
    return checklv.map(questionListShow =>
      <Col span={6}>
        <List QueList={questionListShow} />
      </Col>
    )
  }

  renderCategories() {
    return this.props.Google.categories.map(category =>
      <Menu.Item key={category.id}>
        <Icon type="pie-chart" />
        <span>{category.name}</span>
      </Menu.Item>
    )
  }

  render() {
    return (
      <Row style={{ paddingTop: "10px" }}>
        <Col span={2}><BackButton /></Col>
        <Col span={20}>
          <Row gutter={[16, 16]} style={{ paddingTop: "10px" }}>
            <Menu style={{ borderRadius: "30px" }} mode="horizontal" onClick={(e) => this.props.handleCategotiesIdFunc(e.key)}>
              {this.renderCategories()}
            </Menu>
            {this.renderListQuestion()}
          </Row>
        </Col>
        <Col span={2}>
        {/* {this.state.questionList.map(res=>{
          <Row></Row>
  })} */}
        </Col>
      </Row>
    )
  }
}

export default SelectionQuestion
