import React, { Component } from 'react'
import { Row, Col, Input, Button, Form, Card } from 'antd';
import style from './CreateQue.module.css'
// import Axios from "../config/axios.setup"

const { TextArea } = Input;
export class CreateQue extends Component {

        state ={
            questionNo: Number,
            ch1: 1,
            ch2: Number,
            ch3: Number,
            ch4: Number,
            Answer: Number
        }
    onChange = e => {
        console.log(e.target.value);
        //check number
    }

    putAnswerFrom = (e) => {
        e.preventDefault();
        // this.props.form.validateFieldsAndScroll((err, value) => {
        //     console.log(err, "outcondition")
        //     console.log(value, "VALUE VALUE")
        //     if (!err) {
        //         console.log("incomdition")
        //         Axios.post('/registerUser', {
        //             username: value.username,
        //             password: value.password,
        //             name: value.name,
        //             email: value.email
        //         })
        //             .then(result => {
        //                 console.log(value,"value is")
        //                 signupOpenNotification()
        //                 console.log(result.response)
        //                 this.props.history.push("/login")
        //             })
        //             .catch(err => {
        //                 console.error(err.response.data.message, "--------")
        //                 signupFailedNotification(err.response.data.message)
        //             })
        //     } else {
        //         console.log("ERROR")
        //     }
        // })
    }


    render() {
        const { getFieldDecorator } = this.props.form
        return (
            <Row>
                <Col>
                    <h2>Question {this.state.questionNo}</h2>
                    <Row type="flex" justify="space-around" align="middle">
                        <Col>
                            <Row type="flex" justify="center" align="middle">
                                <Col>
                                <Row type="flex" justify="center">
                                    <Col><Card type="flex" justify="center" align="middle" className={style.paddingCard} ><p style={{margin:0}}>{this.state.ch1}</p></Card></Col>
                                    <Col><Card type="flex" justify="center" align="middle" className={style.paddingCard} ><p style={{margin:0}}>{this.state.ch2}</p></Card></Col>
                                </Row>
                                <Row type="flex" justify="center">
                                    <Col><Card className={style.paddingCard} ><p style={{margin:0}}>{this.state.ch3}</p></Card></Col>
                                    <Col><Card className={style.paddingCard} ><p style={{margin:0}}>{this.state.ch4}</p></Card></Col>
                                </Row>                                
                                </Col>
                                <Col>
                                    <Card className={style.paddingAnswer} ><p style={{margin:0}}>Ans</p></Card>
                                </Col>
                            </Row>
                            <Row>

                                <Form onSubmit={this.putAnswerFrom} onChange={this.onChange} >
                                    <Form.Item type="flex" label="Please Input Question:">
                                        {getFieldDecorator("field", {
                                            rules: [
                                                {
                                                    required: true,
                                                    message: 'Please input an equation!',
                                                }
                                            ]
                                        })(<input />)}
                                    </Form.Item>
                                    <Form.Item type="flex" justify="center">
                                        <Button type="primary" htmlType="submit"  >create</Button>
                                    </Form.Item>
                                </Form>


                            </Row>
                        </Col>
                    </Row>
                    <hr />
                    <Row type="flex" justify="space-around" align="middle">
                        <Col>
                            <Button block type="primary" shape="round"
                                className={style.ButtonCh}>
                                Create
        </Button>
                        </Col>
                        <Col>
                            <Button block type="primary" shape="round"
                                className={style.ButtonCh}>
                                Done
        </Button>
                        </Col>
                    </Row>
                </Col>
            </Row>
        )
    }
}
const WrappedAdvancedSearchForm = Form.create({ name: 'advanced_search' })(CreateQue);

export default WrappedAdvancedSearchForm
