import React, { Component } from 'react'
import { Row, Col,Input,Button,Form } from 'antd';
import Axios from "../config/axios.setup"
import { signupOpenNotification,signupFailedNotification} from "../components/Notification/SignupNotification"

export class Signup extends Component {

      submitFrom =(e) => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err,value)=>{
          if(!err){
            Axios.post('/registerUser',{
              username:value.username,
              password:value.password,
              name:value.name
            })
            .then(result =>{
              console.log(value)
              this.props.history.push("/login")
            })
            .catch(err =>{
              console.error(err)
            })
          }
        })
      }

    render() {
        return (
<Row type = "flex" justify="center" align="middle" style ={{height:"100vh"}}>
    <Col>
<Row  >
<Col type = "flex" justify="center" align="middle">
<img src ="https://f.ptcdn.info/202/059/000/pdj96y10o03Td4WPRuk2-o.jpg" style ={{width:"20%"}}  />
</Col>
</Row>

<Form onSubmit={this.submitFrom} >
<Row gutter={[16, 24]}>
<Form.Item>username : 
                    {getFieldDecorator("username",{
                      rules:[
                              { 
                                required: true, 
                                message: 'Please input your nickname!', 
                              }
                      ]
                    })(<input />)}
                  </Form.Item>
</Row>

<Row gutter={[16, 24]}>
    <Form.Item>
<Col>
Password
<Input type="password" placeholder="Password"></Input>
</Col>
</Form.Item>
</Row>

<Row gutter={[16, 24]}>
    <Form.Item>
<Col>
ConfirmPassword
<Input type="password" placeholder="ConfirmPassword"></Input>
</Col>
    </Form.Item>
</Row>

<Row gutter={[16, 24]}>
    <Form.Item>
<Col>
Email:
<Input placeholder="Email"></Input>
</Col>
    </Form.Item>
</Row>
<Row type = "flex" justify="center" align="middle" style={{margin:"10px"}} >
<Col>
<Form.Item>
<Button type="primary" >Signup</Button>
</Form.Item>
</Col>
</Row>
</Form>

    </Col>
</Row>
        )
    }
}

export default Signup
